    private void bnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bnGuardarActionPerformed
        
         Cliente cliente = new Cliente();
        Ctr_Cliente controlCiente = new Ctr_Cliente();

        if (!txtNombre.getText().isEmpty() && !txtapellido.getText().isEmpty() && !txtcedula.getText().isEmpty()) {
            //JOptionPane.showMessageDialog(null, "Correcto");

            if (!controlCiente.existeCliente(txtcedula.getText().trim())) {
                
                cliente.setNombre(txtNombre.getText().trim());
                cliente.setApellido(txtapellido.getText().trim());
                cliente.setDni(txtcedula.getText().trim());
                cliente.setTelefono(txtTelefono.getText().trim());
                cliente.setDireccion(txtDireccion.getText().trim());
                cliente.setEstado(1);

                if (controlCiente.guardar(cliente)) {
                    JOptionPane.showMessageDialog(null, "Registro guardado");
                    txtNombre.setBackground(Color.green);
                    txtapellido.setBackground(Color.green);
                    txtcedula.setBackground(Color.green);
                    txtTelefono.setBackground(Color.green);
                    txtDireccion.setBackground(Color.green);
                } else {
                    JOptionPane.showMessageDialog(null, "Error al guardar");
                }

            } else {
                JOptionPane.showMessageDialog(null, "El Cliente ya esta registrado");
                txtNombre.setBackground(Color.white);
                txtapellido.setBackground(Color.white);
                txtcedula.setBackground(Color.white);
                txtTelefono.setBackground(Color.white);
                txtDireccion.setBackground(Color.white);
            }

        } else {
            JOptionPane.showMessageDialog(null, "Completa todos los campos");
            txtNombre.setBackground(Color.red);
            txtapellido.setBackground(Color.red);
            txtcedula.setBackground(Color.red);
            txtTelefono.setBackground(Color.red);
            txtDireccion.setBackground(Color.red);
            txtNombre.setForeground(Color.white);
             txtapellido.setBackground(Color.white);
            txtcedula.setBackground(Color.white);
            txtTelefono.setBackground(Color.white);
            txtDireccion.setBackground(Color.white);
        }
        //metodo limpiar
        this.Limpiar();
    }//GEN-LAST:event_bnGuardarActionPerformed

